svg.script:
(function() {
  // znajdź wszystkie grupy .city w tym SVG
  var svg = document.currentScript.ownerSVGElement || document.querySelector('svg');
  if (!svg) return;

  var cities = svg.querySelectorAll('.city');

  function handleCityClick(evt) {
    var clicked = evt.currentTarget;

    // 1) zamknij inne miasta (tylko jedno może być "zamrożone")
    cities.forEach(function(city) {
      if (city !== clicked) {
        city.classList.remove('open');
      }
    });

    // 2) przełącz (toggle) stan klikniętego miasta
    if (clicked.classList.contains('open')) {
      clicked.classList.remove('open');
    } else {
      clicked.classList.add('open');
    }
  }

  // podpinamy klik do każdej grupy .city
  cities.forEach(function(city) {
    city.addEventListener('click', handleCityClick);
  });
})();
